import type { AnimalDefinition } from '@faker-js/faker/definitions/animal';
import type { ColorDefinition } from '@faker-js/faker/definitions/color';
import type { CommerceDefinition } from '@faker-js/faker/definitions/commerce';
import type { CompanyDefinition } from '@faker-js/faker/definitions/company';
import type { DatabaseDefinition } from '@faker-js/faker/definitions/database';
import type { DateDefinition } from '@faker-js/faker/definitions/date';
import type { FinanceDefinition } from '@faker-js/faker/definitions/finance';
import type { HackerDefinition } from '@faker-js/faker/definitions/hacker';
import type { InternetDefinition } from '@faker-js/faker/definitions/internet';
import type { LocationDefinition } from '@faker-js/faker/definitions/location';
import type { LoremDefinition } from '@faker-js/faker/definitions/lorem';
import type { MetadataDefinition } from '@faker-js/faker/definitions/metadata';
import type { MusicDefinition } from '@faker-js/faker/definitions/music';
import type { PersonDefinition } from '@faker-js/faker/definitions/person';
import type { PhoneNumberDefinition } from '@faker-js/faker/definitions/phone_number';
